function SendEmail(n){switch(arguments.length){case 0:$.ajax({url:"/AppServices/Email",data:{mailFrom:$(".mailFrom").val(),siteNumber:$("#mailTo").val(),mailIdentity:$(".mailIdentity").val(),mailSubject:$(".mailSubject").val(),message:$(".mailMsg").val()},type:"POST",cache:!1,dataType:"json"}).success(function(n){EmailSuccess(n)}).error(function(){$("#Status").html("Não foi possivel enviar a mensagem")});break;case 1:$.ajax({url:"/AppServices/Email",data:{mailFrom:$(".mailFrom").val(),siteNumber:n,mailIdentity:$(".mailIdentity").val(),mailSubject:$(".mailSubject").val(),message:$(".mailMsg").val()},type:"POST",cache:!1,dataType:"json"}).success(function(n){$("#Status").html(n)}).error(function(){$("#Status").html("Não foi possivel enviar a mensagem")})}}